{

  const [inputs,setInputs]= useState({})


  const handleChange = (event)=>
  {
    const username = event.target.username;
    const value = event.target.value;
    setInputs(values => ({values, [username]:value}));
    
  }
  const handleSubmit = (event)=>
  {
    event.preventDefault();
    console.log(inputs);
  } 
    return (
        <div className="design">
        <div className="container">
          <form className="login-form" onSubmit={handleSubmit}>
            <h1>LOGIN</h1>
            <label htmlFor="username">Username</label>
            <input type="text" id="username" placeholder=" " autoComplete="off" className="form-control-material" required onChange={handleChange}/>
            <label htmlFor="password">Password</label>
            <input type="password" id="password" placeholder=" " autoComplete="off" className="form-control-material" required />
            <div class = "multiple-choice-login">
            <button className="button-login" type="submit">Log In</button>
            <a href='./Register.js'>Register</a>
            </div>
          </form>
        </div>
      </div>
     
    )
}
export default Login
